(function(){

	tinymce.create('tinymce.plugins.image_mykees',{

		init : function(ed, url){
			ed.addCommand('open_image', function(){

				var el = ed.selection.getNode();
				var url = ed.settings.image_mykees_explorer;
				if(el.nodeName == 'IMG'){
					url = ed.settings.image_mykees_edit
					url += '?src='+encodeURIComponent(ed.dom.getAttrib(el,'src'))+'&alt='+ed.dom.getAttrib(el,'alt')+'&class='+ed.dom.getAttrib(el,'class');
				}
				
				ed.windowManager.open({
					file : url,
					id: 'image_mykees',
					width: 1000,
					height: 600,
					inline: true,
					title : 'Insérer une image'
				},{
					plugin_url : url
				});

			});
			ed.addButton('image',{
				title : 'Insérer une image',
				cmd : 'open_image'
			})
		}
	});

	tinymce.PluginManager.add('image_mykees',tinymce.plugins.image_mykees);
})();